const burger=document.getElementById("burger"),nav=document.getElementById("nav"),page=document.getElementById("page"),body=document.body;function showMenu(){burger.classList.add("active"),nav.classList.add("active")}function closeMenu(){burger.classList.remove("active"),nav.classList.remove("active")}burger.addEventListener("click",e=>{(burger.classList.contains("active")?closeMenu:showMenu)()});